import { Component, OnInit } from '@angular/core';
@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.css']
})

export class FormComponent implements OnInit
{
  name: string = '';
  lastname: string = '';
  constructor(){ }
  ngOnInit(): void {

  }

  onSubmit(){
    let message = 'Hello, I am ${this.name} ${this.lastname}';
    alert(message)
  }
}
<link
  rel="stylesheet"
  href="https://cdn.jsdelivr.net/npm/bootstrap@4.4.1/dist/css/bootstrap.min.css"
/>
<div id="app">
<!-- <form> -->
<div class="custom-container">
    <div class="form-group">
      <label for="name" class="form-label">Name</label>
      <input id = "input1" type="text"  [(ngModel)]="name"/>
    </div>
    <div class="form-group">
      <label for="lastname" class="form-label">Lastname</label>
      <input id = "input2" type="text" placeholder="your lastname" required class="form-control"/>
    </div>
    <div class="form-field">
      <button id="btn" type="submit" (click)="onSubmit()">Submit</button>
    </div>
</div>
